package com.inyaw.admin.base;import lombok.Setter;import lombok.experimental.Accessors;@Setter@Accessors(chain = true)public class BaseDto {    private Integer page = 1;    private Integer size = 10;    public Integer getPage() {        if (page > 0) {            return page - 1;        }        return page;    }    public Integer getSize() {        return size;    }}