package com.inyaw.admin.blog.service;import com.inyaw.admin.blog.bean.InyaaBlogTag;import com.inyaw.admin.blog.dao.InyaaBlogTagDao;import lombok.RequiredArgsConstructor;import org.springframework.stereotype.Service;import java.util.List;@Service@RequiredArgsConstructorpublic class InyaaBlogTagService {    private final InyaaBlogTagDao inyaaBlogTagDao;    public List<InyaaBlogTag> findAll() {        return inyaaBlogTagDao.findAll();    }    public void save(InyaaBlogTag tag) {        inyaaBlogTagDao.save(tag);    }    public void delete(InyaaBlogTag tag) {        inyaaBlogTagDao.delete(tag);    }}