package com.inyaw.admin.blog.service;import com.inyaw.admin.blog.bean.InyaaBlogType;import com.inyaw.admin.blog.dao.InyaaBlogTypeDao;import lombok.RequiredArgsConstructor;import org.springframework.stereotype.Service;import java.util.List;@Service@RequiredArgsConstructorpublic class InyaaBlogTypeService {    private final InyaaBlogTypeDao inyaaBlogTypeDao;    public List<InyaaBlogType> findAll() {        return inyaaBlogTypeDao.findAll();    }    public void save(InyaaBlogType type) {        inyaaBlogTypeDao.save(type);    }    public void delete(InyaaBlogType type) {        inyaaBlogTypeDao.delete(type);    }}