package com.inyaw.admin.feign;import com.inyaw.admin.base.BaseResult;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.multipart.MultipartFile;import java.util.List;@FeignClient("inyaa-picture")public interface InyaaSysFileFeign {    @PostMapping("/file/upload")    BaseResult<String> upload(@RequestParam("file") MultipartFile file, @RequestParam("type") Integer type);    @PostMapping("/file/delete")    BaseResult<String> delete(@RequestParam("id") Integer id);    @PostMapping("/file/list")    BaseResult<List<Object>> list();    @GetMapping("/file/image")    BaseResult<String> getPc(@RequestParam(value = "type") Integer type);}